import React from 'react';
import { Helmet } from 'react-helmet';
import "../ProductDetails/ProductDetails.css"

import styled from "styled-components";
import { Add, Remove } from "@mui/icons-material";
import { mobile } from "../responsive";

const Container = styled.div``;
const Wrapper = styled.div`
    padding: 50px;
    display: flex;
    ${mobile({ padding: "10px", flexDirection: "column" })}
`;
const ImgContainer = styled.div`
    flex:1;
`;
const Image = styled.img`
    width: 100%;
    height: 90vh;
    object-fit: cover;
    ${mobile({ height: "50vh" })}
`;
const InfoContainer = styled.div`
    flex:1; 
    padding: 0px 50px;
    ${mobile({ padding: "10px" })}
`;
const Title = styled.h1`
    font-weight: 200;
`;
const Desc = styled.p`
    margin: 20px 0px;
`;
const Price = styled.span`
    font-weight: 100;
    font-size: 40px;
`;
const FilterContainer = styled.div`
    width: 50%;
    margin: 30px 0px;
    display: flex;
    justify-content: space-between;
    ${mobile({ width: "100%" })}
`;
const Filter = styled.div`
    display: flex;
    align-items: center;
`;
const FilterTitle = styled.span`
    font-size: 20px;
    font-weight: 200;
`;
const FilterColor = styled.div`
    width: 20px;
    height: 20px;
    border-radius: 50%;
    background-color: ${props => props.color};
    margin: 0px 5px;
    cursor: pointer;
`;
const FilterSize = styled.select`
    margin-left: 10px;
    padding: 5px;
`;
const FilterSizeOption = styled.option``;
const AddContainer = styled.div`
    width: 50%;
    display: flex;
    align-items: center;
    justify-content: space-between;
    ${mobile({ width: "100%" })}
`;
const AmountContainer = styled.div`
    display: flex;
    align-items: center;
    font-weight: 700;
`;
const Amount = styled.span`
    width: 30px;
    height: 30px;
    border-radius: 10px;
    border: 1px solid teal;
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 0px 5px;
`;
const Button = styled.button`
    padding: 15px;
    border: 2px solid teal;
    background-color: white;
    cursor: pointer;
    font-weight: 500;
    &:hover{background-color: #f8f4f4;}
`;

const ProductDetails = () => {
  return (
    <Container>
      {/* Thêm các thông tin chi tiết về sản phẩm */}
      <Wrapper>
        <ImgContainer>
          <Image src="https://i.pinimg.com/originals/fd/30/48/fd3048a75fce253abfcddcbecc82e8e7.jpg" alt="Hình ảnh sản phẩm" />
        </ImgContainer>
        <InfoContainer>
          <Title>Tên sản phẩm</Title>
          <Desc>Mô tả sản phẩm</Desc>
          <Price>Giá: $99</Price>
          {/* ... */}
          {/* Thêm thẻ meta Open Graph */}
          <Helmet>
            <meta property="og:title" content="Tên sản phẩm" />
            <meta property="og:image" content="path/to/https://i.pinimg.com/originals/fd/30/48/fd3048a75fce253abfcddcbecc82e8e7.jpgproduct-image.jpg" />
            <meta property="og:image" content="URL_hình_ảnh" />
            {/* ... */}
            {/* Nội dung chi tiết sản phẩm */}
            <meta property="og:description" content="Mô tả sản phẩm" />
          </Helmet>
        </InfoContainer>
      </Wrapper>
    </Container>
  );
};

export default ProductDetails;
