import React from 'react';
import { Helmet } from 'react-helmet';
import "../ProductDetails/ProductDetails.css"

const ProductDetails = () => {
  return (
    <div className='product-detail-container'>
      <div className='product-detail'>
        <h1 className='product-title'>Tên sản phẩm</h1>
        <img className='product-image' src="https://i.pinimg.com/originals/fd/30/48/fd3048a75fce253abfcddcbecc82e8e7.jpg" alt="Hình ảnh sản phẩm" />
        <p className='product-description'>Mô tả sản phẩm</p>
        <p>Giá: $99</p>
        {/* ... */}

        {/* Thêm thẻ meta Open Graph */}
        <Helmet>
          <meta property="og:title" content="Tên sản phẩm" />
          <meta property="og:image" content="path/to/https://i.pinimg.com/originals/fd/30/48/fd3048a75fce253abfcddcbecc82e8e7.jpgproduct-image.jpg" />
          <meta property="og:image" content="URL_hình_ảnh" />
          {/* ... */}
          {/* Nội dung chi tiết sản phẩm */}
          <meta property="og:description" content="Mô tả sản phẩm" />
        </Helmet>
      </div>
    </div>
  );
};

export default ProductDetails;
